#include <fstream>
#include <iostream>
#include <vector>
using namespace std;
int main(int argc, char** argv)
{
    int packets = 10'000;
    int time = 0;
    std::ofstream dev("/dev/ttyACM0");
    std::ifstream read_time("/dev/ttyACM0");
    if(!dev)
    {
        std::cout<<"Error opening file"<<std::endl;
        return 1;
    }
    std::vector<size_t> sizes;
    std::ofstream log("res_out_new.csv", std::ios_base::out | std::ios_base::app);
    if (argc > 1)
    {
        for (size_t i = 1; i < argc; ++i)
        {
            sizes.push_back(strtol(argv[i], nullptr, 10));
            if (errno == ERANGE)
                return 1;
        }
    }
    const char* s =
            "";
    std::string cmd = "212s";
    for(int j =0; j < 10; ++j)
    for(size_t size_of_packet: sizes)
    {
        cout<<"Begin "<<size_of_packet<<endl;
        int i = 0;
        dev.write(cmd.c_str(), cmd.length());
        dev.flush();
        while (++i < 10000)
        {
            dev._M_write(s, size_of_packet);//_M_write
            dev.rdbuf()->pubsync();
        }
        dev.write("212e", sizeof("212e"));
        dev.flush();
        read_time.read(reinterpret_cast<char*>(&time), sizeof(int));
        log << size_of_packet << ", " << static_cast<double>(packets * size_of_packet) * 100'000 / time << ", "
            << static_cast<double>(time) / 100'000 << ", " << packets << std::endl;
    }
    dev.close();
    read_time.close();
    log.close();
}
